package classdemo;

public class DataTypes {
	static boolean valueB;
	static double valueD;
	static float valueF;
	static int valueI;
	static long valueL;
	static char valueC;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	      System.out.println("Default value of boolean=" + valueB);
	      System.out.println("Default value of double=" + valueD);
	      System.out.println("Default value of float=" + valueF);
	      System.out.println("Default value of int=" + valueI);
	      System.out.println("Default value of long=" + valueL);
	      System.out.println("Default value of char=" + valueC);
	      System.out.println();
	      System.out.println("Range of byte="+Byte.MIN_VALUE+"-"+Byte.MAX_VALUE);
	      System.out.println("Range of int="+Integer.MIN_VALUE+"-"+Integer.MAX_VALUE);
	      System.out.println("Range of float="+Float.MIN_VALUE+"-"+Float.MAX_VALUE);
	      System.out.println("Range of char="+Character.MIN_VALUE+"-"+Character.MAX_VALUE);
	      System.out.println("Range of double="+Double.MIN_VALUE+"-"+Double.MAX_VALUE);
	      System.out.println("Range of long="+Long.MIN_VALUE+"-"+Long.MAX_VALUE);


	}

}
